import React, { useState } from "react";
import "../css/index.css";
import Personal from "../cv/Personal";

const Preview = ({ personal, id }) => {
    const [showEditForm, setEditForm] = useState({
        EditMode: true,
    });
    const [name, setName] = useState(
        personal.map((personalType) => {
            return personalType.name
        })
    );
    const [email, setEmail] = useState(
        personal.map((personalType) => {
            return personalType.email
        })
    );
    const [phone, setPhone] = useState(
        personal.map((personalType) => {
            return personalType.phone
        })
    );

    const [person, setPerson] = useState(personal)

    const handleSubmitEdit = (e) =>{
        e.preventDefautl();
        const updatedPerson = {id, name, email, phone};
        setPerson(person.map((editedPerson)=>editedPerson.id === id ? updatedPerson : person));   
    }

    const editForm = () => {
        setEditForm({
            ...showEditForm,
            EditMode: false,
        });
    };
    return showEditForm.EditMode ? (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                </div>
            </div>
        )) : (
        <Personal/>
    )
}
export default Preview;



