import React from "react";
import index from "../css/index.css";

const Preview = ({ personal, editing, setEditing, setPersonalInfo }) => {

    let viewMode = {};
    let editMode = {};

    if (editing) {
        viewMode.display = "none";
    } else {
        editMode.display = "none";
    }

    const handleEditing = () =>{
        setEditing({
            editing: true
        })
    }

    return (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                            <input type="text" className={index.textInput} name="name" style={editMode} value={personalType.name} onChange={e => setPersonalInfo({
                                ...personal,
                                name: e.target.value
                            })}/>
                             <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                            <input type="text" className={index.textInput} name="name" style={editMode} value={personalType.email} onChange={e => setPersonalInfo({
                                ...personal,
                                email: e.target.value
                            })}></input>
                             <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                            <input type="text" className={index.textInput} name="name" style={editMode} value={personalType.phone} onChange={e => setPersonalInfo({
                                ...personal,
                                phone: e.target.value
                            })}></input>
                             <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    </div>
                </div>
            </div>
        )
    )
}

export default Preview;
