import React from "react";
import styles from "../css/index.css";

const Preview = ({ personal, handleEditing, setUpdate, editing, handleUpdate }) => {

    let viewMode = {};
    let editMode = {};

    if (editing) {
        viewMode.display = "none"
    } else {
        editMode.display = "none"
    }

    return (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                        <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                        <input type="text" className={styles.textInput} style={editMode} value={personalType.name} onChange={(e) => setUpdate(e.target.value, personalType.name)} onKeyDown={handleUpdate}></input>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                        <input type="text" className={styles.textInput} style={editMode} value={personalType.email} onChange={(e) => setUpdate(e.target.value, personalType.email)} onKeyDown={handleUpdate}></input>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                        <input type="text" className={styles.textInput} style={editMode} value={personalType.phone} onChange={(e) => setUpdate(e.target.value, personalType.phone)} onKeyDown={handleUpdate}></input>
                    </div>
                </div>
            </div>
        ));

}

export default Preview;
