import React, { useState } from "react";
import "../css/index.css";
import Personal from "../cv/Personal";

const Preview = ({personal, mode}) => {
    const [showEditForm, setEditForm] = useState({
        EditMode: true,
    });

    const editForm = () => {
        setEditForm({
            ...showEditForm,
            EditMode: mode,
        });
    };
    return showEditForm.EditMode ? (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                        <button className="button-edit" type="button" onClick={editForm}>Edit Task</button>

                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button className="button-edit" type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button className="button-edit" type="button" onClick={editForm}>Edit Task</button>
                    </div>
                </div>
            </div>
        )) : (
        <Personal/>
    )
}
export default Preview;
