import React, { Component } from "react";
import uniqid from "uniqid";
import Preview from "./preview";

class Education extends Component {
    constructor() {
        super()
        this.state = {
            School: {
                name: "",
                id: uniqid()
            },
            Study: {
                title: "",
                id: uniqid()
            },
            StudyDate: {
                date: "",
                id: uniqid()
            },
            StudyEnd:{
                date: "",
                id: uniqid()
            },
            tasks: [],
        };
    }

    handleSchoolChange = (e) =>{
        this.setState = ({
            name: e.target.value,
            id: this.state.School.id
        });
    }
    handleStudyTitleChange = (e) =>{
        this.setState = ({
            name: e.target.value,
            id: this.state.Study.id
        });
    }
    handleStudyDateChange = (e) =>{
        this.setState = ({
            task:{
                text: e.target.value,
                id: this.state.StudyDate.id
            },
        });
    }
    handleStudyDateEndChange = (e) =>{
        this.setState = ({
            task:{
                text: e.target.value,
                id: this.state.StudyEnd.id
            },
        });
    }

    render() {
        const {task, tasks} = this.state;
        return (
            <div>
                <form>
                    <div className="educational-info">
                        <label>School Name: </label>
                        <input type="text" value={task.text} onChange={this.handleChange}></input>
                    </div>
                    <div className="educational-info">
                        <label>Title of Study: </label>
                        <input type="text" value={task.text} onChange={this.handleChange}></input>
                    </div>
                    <div className="educational-info">
                        <label>Date of Study from: </label>
                        <input type="date" value={task.text} onChange={this.handleChange}></input>
                    </div>
                    <div className="educational-info">
                        <label>Date of Study to: </label>
                        <input type="date" value={task.text} onChange={this.handleChange}></input>
                    </div>
                </form>
                <Preview tasks = {tasks}/>
            </div>
        )
    }
}

export default Education;
