import React, { useState } from "react";
import "../css/index.css";
import EditForm from "../cv/EditForm";
const Preview = ({ personal }) => {

    const [showEditForm, setEditForm] = useState({
        EditMode: true,
    });


    const [name, setName] = useState(
        personal.map((personalType) => {
            return personalType.name
        })
    );

    const [email, setEmail] = useState(
        personal.map((personalType) => {
            return personalType.email
        })
    );
    const [phone, setPhone] = useState(
        personal.map((personalType) => {
            return personalType.phone
        })
    );

    console.log(name);
    const editForm = () => {
        setEditForm({
            ...showEditForm,
            EditMode: false,
        });
    };

    return showEditForm.EditMode ? (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                </div>
            </div>
        )) : (
        <EditForm personName={name} personEmail={email} personPhone={phone} personKey={personalType.id} personArray={personal} />
    )

}

export default Preview;
