import React from "react";
import index from "../css/index.css";

const Preview = ({ personal, handleEditing, setNameUpdate, handleUpdate, editing }) => {

    let viewMode = {};
    let editMode = {};

    if (editing) {
        viewMode.display = "none";
    } else {
        editMode.display = "none";
    }

    const personalName = personal.map((personalType) =>
        <div key={personalType.id}>
            <div className="preview-info">
                <div>
                    <p className="text-info">
                        <label>Name:
                            {personalType.name}
                        </label>
                    </p>
                    <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    <input type="text" className={index.textInput} style={editMode} value={personalType.name ?? ""} onChange={(e) => setNameUpdate(e.target.value, personalType.id)} onKeyDown={handleUpdate}></input>
                </div>
                <div>
                    <p className="text-info">
                        <label>Email:
                            {personalType.email}
                        </label>
                    </p>
                    <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    <input type="text" className={index.textInput} style={editMode} value={personalType.email ?? ""} onChange={(e) => setUpdate(e.target.value, personalType.id)} onKeyDown={handleUpdate}></input>
                </div>
                <div>
                    <p className="text-info">
                        <label>Phone:
                            {personalType.phone}
                        </label>
                    </p>
                    <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    <input type="text" className={index.textInput} style={editMode} value={personalType.phone ?? ""} onChange={(e) => setUpdate(e.target.value, personalType.id)} onKeyDown={handleUpdate}></input>
                </div>
            </div>
        </div>
    );


    return (
        <div>{personalName}</div>
    );
}

export default Preview;
