import React, { Component } from "react";
import uniqid from "uniqid";
import Preview from "./preview";

class Personal extends Component {
    constructor() {
        super()
        this.state = {
            task: {
                text: "",
                id: uniqid()
            },
            tasks: [],
        };
    }

    handleChange = (e) => {
        this.setState({
            task: {
                text: e.target.value,
                id: this.state.id
            },
        });
    }

    render() {
        const {task, tasks} = this.state;
        return (
            <div>
                <form>
                    <div className="personal-info">
                        <label>Name: </label>
                        <input type="text" value={task.text} onChange={this.handleChange}></input>
                    </div>
                    <div className="personal-info">
                        <label>Email: </label>
                        <input type="text" value={task.text} onChange={this.handleChange}></input>
                    </div>
                    <div className="personal-info">
                        <label>Phone:</label>
                        <input type="tel" value={task.text} onChange={this.handleChange}></input>
                    </div>
                </form>
                <Preview tasks = {tasks}/>
            </div>
        )
    }
}

export default Personal;
