import React, { useState } from "react";
import "../css/index.css";
import EditForm from "../cv/EditForm";
const Preview = ({ personal }) => {

    const [showEditForm, setEditForm] = useState({
        EditMode: true,
        FormEdit: false
    });

    const editForm = () => {
        setEditForm({
            ...showEditForm,
            EditMode: false,
        });
    };

    return showEditForm.EditMode ? (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                </div>
            </div>
        )) : (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <EditForm personName={personalType.name} personEmail={personalType.email} personPhone={personalType.phone} personKey={personalType.id} showEditForm={showEditForm} setEditForm={setEditForm} />
            </div>
        ))

}

export default Preview;
