import React, { useState } from "react";
import "../css/index.css";
const Preview = ({ personal }) => {

    const [person, setPerson] = useState("");

    const [isEditing, setIsEditing] = useState(false);

    const [currentPerson, setCurrentPerson] = useState({})

    const handleInputChange = (e) => {
        setPerson(e.target.value);
    };

    const handleEditInputChange = (e) => {
        setCurrentPerson({ ...currentPerson, name: e.target.value });
    }

    const handleFormSubmit = (e) => {
        e.preventDefault();

        if (person !== "") {
            setPerson([
                ...personal,
                {
                    id: person.length + 1,
                    name: person.trim()
                }
            ]);
        }
        setPerson("");
    }

    const handleEditFormSubmit = (e) => {
        e.preventDefault();
        handleUpdatePerson(currentPerson.id, currentPerson);
    }

    const handleUpdatePerson = (id, updatedPerson) => {
        const updatePerson = personal.map((person) => {
            return person.id === id ? updatedPerson : person
        });
        setIsEditing(false);
        setPerson(updatePerson);
    }

    const handleEditClick = (person) => {
        setIsEditing(true);
        setCurrentPerson({ ...person })
    }

    return (
        <div>
            {isEditing ? (
                <form onSubmit={handleEditFormSubmit}>
                    {/* <div className="button-container">
                        <button className="button-submit">Preview Personal Info</button>
                    </div> */}
                    <div className="personal-info">
                        <label>Name: </label>
                        <input type="text" name="name" value={currentPerson.name} onChange={handleEditInputChange}></input>
                    </div>
                    {/* <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={personal.email} onChange={handleEmailInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={personal.phone} onChange={handlePhoneInfo}></input>
                </div> */}
                    <button type="submit">Update</button>
                    <button onClick={() => setIsEditing(false)}>Cancel</button>
                </form>
            ) : (
                <form onSubmit={handleFormSubmit}>
                    <div className="button-container">
                        <button className="button-submit">Preview Personal Info</button>
                    </div>
                    <div className="personal-info">
                        <label>Name: </label>
                        <input type="text" name="name" value={person} onChange={handleInputChange}></input>
                        <button type="submit">Add</button>
                    </div>
                </form>
            )}

            <div>
                {personal.map((personalType) =>
                    <div key={personalType.id}>
                        <div className="preview-info">
                            <div className="text-info">
                                <label>Name: </label>
                                {personalType.name}
                                <button type="button" onClick={() => handleEditClick(person)}>Edit Task</button>
                            </div>
                        </div>
                        {/* <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div> */}
                    </div>
                )}
            </div>
        </div>
    );
}

export default Preview;
