    return (
        <div>
            <form onSubmit={handleSubmitEdit}>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={personName} onChange={(e)=>setName(e.target.value)}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={personEmail} onChange={(e)=>setEmail(e.target.value)}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={personPhone} onChange={(e)=>setPhone(e.target.value)}></input>
                </div>
            </form>
            <button type="button">Save Form</button>
        </div>
    )

}
export default EditForm;
