import React, { useContext, useState } from "react";
// import Preview from "../cv-preview/PreviewPersonal-info";
const EditForm = ({personName, personEmail, personPhone, personId, setEditForm}) => {

    const [name, setName] = useState(personName);
    const [email, setEmail] = useState(personEmail);
    const [phone, setPhone] = useState(personPhone);


    const handleSubmit = (e) =>{
        e.preventDefault();
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={name} onChange={(e)=>setName(e.target.value)}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={email} onChange={(e)=>setEmail(e.target.value)}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={phone} onChange={(e)=>setPhone(e.target.value)}></input>
                </div>
            </form>
            <button>Update Form</button>
        </div>
    )

}
export default EditForm;
