import React from "react";
import index from "../css/index.css";

const Preview = ({ person, personal, editing, setEditing, setPersonalInfo }) => {

    let viewMode = {};
    let editMode = {};

    if (editing) {
        viewMode.display = "none";
    } else {
        editMode.display = "none";
    }


    return (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div>
                        <p className="text-info">
                            <label>Name:
                                {personalType.name}
                                <input type="text" className={index.textInput} name="name" style={editMode} value={person.name} onChange={e => setPersonalInfo({
                                    ...personal,
                                    name: e.target.value
                                })}></input>
                            </label>
                        </p>
                    </div>
                    <div>
                        <p className="text-info">
                            <label>Email:
                                {personalType.email}
                                <input type="text" className={index.textInput} name="name" style={editMode} value={person.email} onChange={e => setPersonalInfo({
                                    ...personal,
                                    email: e.target.value
                                })}></input> 
                            </label>
                        </p>
                    </div>
                    <div>
                        <p className="text-info">
                            <label>Phone:
                                {personalType.phone}
                                <input type="text" className={index.textInput} name="name" style={editMode} value={person.phone} onChange={e => setPersonalInfo({
                                    ...personal,
                                    phone: e.target.value
                                })}></input>
                            </label>
                        </p>
                    </div>
                </div>
            </div>
        )
    )
}

export default Preview;
