import React, { useState } from "react";
import Preview from "../cv-preview/PreviewPersonal-info";
import uniqid from "uniqid";

const Personal = () => {
    const initialPersonalInfo = {
        Person:{
        name: "",
        email: "",
        phone: "",
        },
        personalInfo: [],
        mode: true
    };

    const [personal, setPersonalInfo] = useState(initialPersonalInfo);
    const [editing, setEditing] = useState(false);


    const handlePersonalInfo = (e) => {
        setPersonalInfo({
            ...personal,
            [e.target.name]: e.target.value,
            id: uniqid()
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setPersonalInfo(currentState => {
            return {
                personalInfo: [...currentState.personalInfo, personal.Person],
                person:{
                    name: "",
                    email: "",
                    phone: "",
                    id: uniqid()
                }
            };
        });
        console.log(personal.personalInfo);
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="button-container">
                    <button className="button-submit">Preview Personal Info</button>
                </div>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={personal.Person.name ?? ""} onChange={handlePersonalInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={personal.Person.email ?? ""} onChange={handlePersonalInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={personal.Person.phone ?? ""} onChange={handlePersonalInfo}></input>
                </div>
            </form>
            <Preview personal={personal.personalInfo} setPersonalInfo={setPersonalInfo} editing={editing} setEditing={setEditing} />
        </div>

    )
}
export default Personal;
