import React from "react";
import Personal from "../cv/Personal";

const Preview = ({ personal, startEditing, mode}) => {



    return (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        <div onClick={startEditing} className="clickableField">{personalType.name} </div> 
               </div>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        <div onClick={startEditing} className="clickableField">{personalType.email} </div> 
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        <div onClick={startEditing} className="clickableField">{personalType.phone}</div> 
                    </div>
                </div>
        )
    )
}

export default Preview;
