import React from "react";
// import index from "../css/index.css";

const Preview = ({ personal, handleEditing, setUpdate, handleUpdate, editing }) => {

    let viewMode = {};
    let editMode = {};

    if (editing) {
        viewMode.display = "none";
    } else {
        editMode.display = "none";
    }

    const personalName = personal.map((personalType) =>
        <div key={personalType.id}>
            <div className="preview-info">
                <div>
                    <p className="text-info">
                        <label>Name:
                            {personalType.name}
                        </label>
                    </p>
                    <button type="button" onClick={handleEditing} style={viewMode}>Edit Task</button>
                    <input type="text" className={styles.textInput} style={editMode} value={task.text} onChange={(e) => setUpdate(e.target.value, personalType.id)} onKeyDown={handleUpdate}></input>
                </div>
                <div>
                    <p className="text-info">{personalType.email}</p>
                </div>
                <div>
                    <p className="text-info">{personalType.phone}</p>
                </div>
            </div>
        </div>
    );


    return (
        <div>{personalName}</div>
    );
}

export default Preview;
