import React, { useState } from "react";
import Preview from "../cv-preview/PreviewPersonal-info";


const Personal = () => {
    const initialPersonalInfo = {
        name: "",
        email: "",
        phone: "",
        personalInfo: [],
        mode: true,
    };


    const [personal, setPersonalInfo] = useState(initialPersonalInfo);

    const [editing, setEditing] = useState(false);

    const handleNameInfo = (e) => {
        setPersonalInfo({
            ...personal,
            name: e.target.value,
        });
        console.log(personal);
    };
    const handleEmailInfo = (e) => {
        setPersonalInfo({
            ...personal,
            email: e.target.value,
        });
        console.log(personal);
    };
    const handlePhoneInfo = (e) => {
        setPersonalInfo({
            ...personal,
            phone: e.target.value,
        });
        console.log(personal);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setPersonalInfo(currentState => {
            return {
                personalInfo: [...currentState.personalInfo, personal],
                    name: "",
                    email: "",
                    phone: "",
            };
        });
        console.log(personal.personalInfo)
    };

    const startEditing = () =>{
        setEditing(true);
    }

    const stopEditing = ()=>{
        setEditing(false);
    }

    return (
        <div>
            {editing ? (
            <form onSubmit={handleSubmit}>
                <div className="button-container">
                    <button className="button-submit">Preview Personal Info</button>
                </div>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={personal.name} onChange={handleNameInfo} onBlur={stopEditing}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={personal.email} onChange={handleEmailInfo} onBlur={stopEditing}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={personal.phone} onChange={handlePhoneInfo} onBlur={stopEditing}></input>
                </div>
            </form> ):(
            <Preview personal={personal.personalInfo} startEditing={startEditing}/>
            )}
        </div>

    )
}
export default Personal;
