import React, { Component } from "react";
import uniqid from "uniqid";
import Preview from "./preview";

class Personal extends Component {
    constructor() {
        super()
        this.state = {
            personalInfo: {
                name: "",
                email: "",
                phone: "",
                id: uniqid()
            },
            tasks: [],
        };
    }

    handleChange = args => (e) => {
        this.setState({
            personalInfo: {
                [args]: e.target.value,
                id: this.state.personalInfo.id
            },
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        const{personalInfo} = this.state;
        this.setState({
            tasks: this.state.tasks.concat(personalInfo),
            personalInfo: {
                name: "",
                email: "",
                phone: "",
                id: uniqid()
            },
        });
    }

    render() {
        const { personalInfo, tasks } = this.state;
        return (
            <div>
                <form onSubmit={this.onSubmit}>
                    <div className="button-container">
                        <button>Preview</button>
                    </div>
                    <div className="personal-info">
                        <label>Name: </label>
                        <input type="text" name="name" value={personalInfo.name} onChange={this.handleChange("name")}></input>
                    </div>
                    <div className="personal-info">
                        <label>Email: </label>
                        <input type="text" email="email" value={personalInfo.email} onChange={this.handleChange("email")}></input>
                    </div>
                    <div className="personal-info">
                        <label>Phone:</label>
                        <input type="tel" name="phone" value={personalInfo.phone} onChange={this.handleChange("phone")}></input>
                    </div>
                </form>
                <Preview tasks={tasks} />
            </div>
        )
    }
}

export default Personal;
