import React, { useState } from "react";
import Preview from "../cv-preview/PreviewPersonal-info";
import uniqid from "uniqid";

const Personal = () => {
    const initialPersonalInfo = {
        name: "",
        email: "",
        phone: "",
        personalInfo: [],
        mode: true,
        editing: false,
        id: uniqid()
    };


    const [personal, setPersonalInfo] = useState(initialPersonalInfo);

    const handleNameInfo = (e) => {
        setPersonalInfo({
            ...personal,
            name: e.target.value,
            id: uniqid()
        });
        console.log(personal);
    };
    const handleEmailInfo = (e) => {
        setPersonalInfo({
            ...personal,
            email: e.target.value,
            id: uniqid()
        });
        console.log(personal);
    };
    const handlePhoneInfo = (e) => {
        setPersonalInfo({
            ...personal,
            phone: e.target.value,
            id: uniqid()
        });
        console.log(personal);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setPersonalInfo(currentState => {
            return {
                personalInfo: [...currentState.personalInfo, personal],
                    name: "",
                    email: "",
                    phone: "",
                    editing: false,
                    mode: false
            };
        });
        console.log(personal.personalInfo)
    };

    const handleEditing = () =>{
        setPersonalInfo({
            ...personal,
            editing: true
        });
    }

    const setUpdate = (updatedText, id) => {
        setPersonalInfo({
            ...personal,
            personalInfo: personal.personalInfo.map((person)=>{
                if(person.id === id){
                    person.text = updatedText;
                };
                return person;
            })
        });
    };

    const handleUpdate = (event) =>{
        if(event.key === "Enter"){
            setPersonalInfo({
                ...personal,
                editing: false
            })
        }
    }

    return personal.mode ? (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="button-container">
                    <button className="button-submit">Preview Personal Info</button>
                </div>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={personal.name} onChange={handleNameInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={personal.email} onChange={handleEmailInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={personal.phone} onChange={handlePhoneInfo}></input>
                </div>
            </form>
        </div>):(
        <Preview personal={personal.personalInfo} handleEditing={handleEditing} editing={personal.editing} setUpdate={setUpdate} handleUpdate={handleUpdate}/>
    )
}
export default Personal;
