import React, { useState } from "react";
// import Preview from "../cv-preview/PreviewPersonal-info";
const EditForm = ({person}) => {

    const [name, setName] = useState(person.name);
    const [email, setEmail] = useState(person.email);
    const [phone, setPhone] = useState(person.phone);
    // let id = personal.id;



    return (
        <div>
            <form>
                <div className="button-container">
                    <button className="button-submit">Preview Personal Info</button>
                </div>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={name} onChange={(e)=>setName(e.target.value)}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={email} onChange={(e)=>setEmail(e.target.value)}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={phone} onChange={(e)=>setPhone(e.target.value)}></input>
                </div>
            </form>
        </div>
    )

}

export default EditForm;
