import React from "react";
import index from "../css/index.css";

const Preview = ({ personal, editing, setEditing, setPersonalInfo }) => {

    let viewMode = {};
    let editMode = {};

    if (editing) {
        viewMode.display = "none";
    } else {
        editMode.display = "none";
    }

    return (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div>
                        <p className="text-info">
                            <label>Name:
                                {personalType.name}
                                <input type="text" className={index.textInput} style={editMode} value={personalType.name}></input>
                                <button type="button" onClick={() => setEditing(true)} style={viewMode}>Edit Task</button>
                            </label>
                        </p>
                    </div>
                    <div>
                        <p className="text-info">
                            <label>Email:
                                {personalType.email}
                                <input type="text" className={index.textInput} style={editMode}></input>
                                <button type="button" onClick={() => setEditing(true)} style={viewMode}>Edit Task</button>
                            </label>
                        </p>
                    </div>
                    <div>
                        <p className="text-info">
                            <label>Phone:
                                {personalType.phone}
                                <input type="text" className={index.textInput} style={editMode}></input>
                                <button type="button" onClick={() => setEditing(true)} style={viewMode}>Edit Task</button>
                            </label>
                        </p>
                    </div>
                </div>
            </div>
        )
    )
}

export default Preview;
