import React, { useState } from "react";
// import styles from "../css/index.css";
import EditForm from "../cv/EditForm";
const Preview = ({personal}) => {


    const [showEditForm, setEditForm] = useState({
        EditMode: true
    });

    console.log(personal.name);
    const editForm = () => {
        setEditForm({
            ...showEditForm,
            EditMode: false
        });
    };

    return showEditForm.EditMode ? (
        personal.map((personalType) =>
            <div key={personalType.id}>
                <div className="preview-info">
                    <div className="text-info">
                        <label>Name: </label>
                        {personalType.name}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Email: </label>
                        {personalType.email}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                    <div className="text-info">
                        <label>Phone: </label>
                        {personalType.phone}
                        <button type="button" onClick={editForm}>Edit Task</button>
                    </div>
                </div>
            </div>
        )):(
            <EditForm personName={personal.name} personEmail={personal.email} personPhone={personal.phone}/>
            )

}

export default Preview;
