import React, { useState } from "react";
import uniqid from "uniqid";
import Preview from "../cv-preview/PreviewPersonal-info";

const Personal = () => {
    const initialPersonalInfo = {
        personal: {
            name: "",
            email: "",
            phone: "",
            id: uniqid(),
            editing: false
        },
        personalInfo: [],
        mode: true
    };

    const [personal, setPersonalInfo] = useState(initialPersonalInfo);


    const handlePersonalInfo = (e) => {
        setPersonalInfo({
            ...personal,
            [e.target.name]: e.target.value,
            id : personal.personal.id
        });

        console.log(personal.personal);
    }
;
    const handleSubmit = (e)=>{
        e.preventDefault();
        setPersonalInfo(currentState=>{
            return{
                personalInfo: [...currentState.personalInfo, personal],
                personal: {
                    name: "",
                    email: "",
                    phone: "",
                    id: uniqid(),
                    editing: false
                },
            };
        });
        console.log(personal.personalInfo);
    };

    const handleEditing = () => {
        setPersonalInfo({
            ...personal,
            editing: true
        });
    };

    const setNameUpdate = (updatedText, id) => {
        setPersonalInfo({
            ...personal,
            personal: personal.personalInfo.map((personal) => {
                if (personal.id === id) {
                    [personal.name] = updatedText;
                };
                return personal;
            })
        });
    };

    const handleUpdate = (event) => {
        if (event.key === "Enter") {
            setPersonalInfo({
                ...personal,
                editing: false
            });
        };
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="button-container">
                    <button className="button-submit">Preview Personal Info</button>
                </div>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={personal.name ?? ""} onChange={handlePersonalInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={personal.email ?? ""} onChange={handlePersonalInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={personal.phone ?? ""} onChange={handlePersonalInfo}></input>
                </div>
            </form>
            <Preview personal ={personal.personalInfo} handleEditing={handleEditing} setUpdate={setNameUpdate} editing={personal.editing} handleUpdate={handleUpdate}/>
        </div>
    )
}
export default Personal;
