import React, { useState } from "react";
import Preview from "../cv-preview/PreviewPersonal-info";
import uniqid from "uniqid";

const Personal = () => {
    const initialPersonalInfo = {
        name: "",
        email: "",
        phone: "",
        personalInfo: [],
        mode: true,
    };


        const initalEdit ={
            showEdit : false,
        }


    const [personal, setPersonalInfo] = useState(initialPersonalInfo);

    const [edit, setEdit] = useState(initalEdit);

    const handleNameInfo = (e) => {
        setPersonalInfo({
            ...personal,
            name: e.target.value,
            id: uniqid()
        });
        console.log(personal);
    };
    const handleEmailInfo = (e) => {
        setPersonalInfo({
            ...personal,
            email: e.target.value,
            id: uniqid()
        });
        console.log(personal);
    };
    const handlePhoneInfo = (e) => {
        setPersonalInfo({
            ...personal,
            phone: e.target.value,
            id: uniqid()
        });
        console.log(personal);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setPersonalInfo(currentState => {
            return {
                personalInfo: [...currentState.personalInfo, personal],
                    name: "",
                    email: "",
                    phone: "",
                    // id: uniqid()
            };
        });
        console.log(personal.personalInfo)
    };

    const toggle = () =>{
        setEdit({
            showEdit:!edit.showEdit 
        });
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="button-container">
                    <button className="button-submit">Preview Personal Info</button>
                </div>
                <div className="personal-info">
                    <label>Name: </label>
                    <input type="text" name="name" value={edit ? "" : personal.name} onChange={handleNameInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Email: </label>
                    <input type="email" name="email" value={edit ? "" : personal.email} onChange={handleEmailInfo}></input>
                </div>
                <div className="personal-info">
                    <label>Phone:</label>
                    <input type="tel" name="phone" value={edit ? "" : personal.phone} onChange={handlePhoneInfo}></input>
                </div>
            </form>
            <Preview personal={personal.personalInfo}/>
            <button className="button-edit" onClick={toggle}>Edit Personal Info</button>
        </div>

    )
}
export default Personal;
